import React, {useContext} from "react";
import {Navbar, Container, Nav, NavItem} from 'reactstrap';
import {Link, NavLink} from "react-router-dom";
import {CurrentUserContext} from 'context/currentUser';

export const TopBar = () => {
  const [currentUserState] = useContext(CurrentUserContext);
  return (
    <Navbar light>
      <Container>
        <Link to={'/'} className={'navbar-brand'}>Medium</Link>
        <Nav className={'pull-xs-right nav'} navbar>
          <NavItem>
            <NavLink to={'/'} className={'nav-link'} exact>
              Home
            </NavLink>
          </NavItem>
          {currentUserState.isLoggedIn === false && (
            <>
              <NavItem>
                <NavLink to={'/login'} className={'nav-link'} exact>
                  Sign in
                </NavLink>
              </NavItem>
              <NavItem>
                <NavLink to={'/register'} className={'nav-link'} exact>
                  Sign up
                </NavLink>
              </NavItem>
            </>
          )}
          {currentUserState.isLoggedIn && (
            <>
              <NavItem>
                <NavLink to={'/articles/new'} className={'nav-link'} exact>
                  <i className={'ion-compose'}/>
                  &nbsp; New Post
                </NavLink>
              </NavItem>
              <NavItem>
                <NavLink to={`/profiles/${currentUserState.currentUser.username}`} className={'nav-link'} exact>
                  <img
                      src={currentUserState.currentUser.image}
                      className={'user-pic'}
                      alt=""/>
                      &nbsp; {currentUserState.currentUser.username}
                </NavLink>
              </NavItem>
            </>
          )}
        </Nav>
      </Container>
    </Navbar>
  )
};
